package apps.tucancha.Controllerimport android.os.Buildimport android.support.annotation.RequiresApiimport apps.tucancha.DAO.DAORenderimport apps.tucancha.Model.Jugadorimport apps.tucancha.Utils.ResultListenerimport com.google.ar.sceneform.rendering.ViewRenderableimport java.util.concurrent.CompletableFuture/** * Created by camilo-de-cruz on 18/11/18. * YOPdev.com */class ControllerRender {    var daoRender: DAORender = DAORender()    @RequiresApi(Build.VERSION_CODES.N)    public fun completeAllFuture(list: List<CompletableFuture<ViewRenderable>>, escuchadorDeLaVista: ResultListener<List<ViewRenderable>>) {        daoRender.completeAllFuture(list, ResultListener<List<ViewRenderable>> { resultado ->            escuchadorDeLaVista.finish(resultado)        })    }}