package apps.tucancha.DAOimport android.os.Buildimport android.support.annotation.RequiresApiimport apps.tucancha.Utils.ResultListenerimport com.google.ar.sceneform.rendering.ViewRenderableimport java.util.concurrent.CompletableFuture/** * Created by camilo-de-cruz on 18/11/18. * YOPdev.com */class DAORender {    @RequiresApi(Build.VERSION_CODES.N)    public fun completeAllFuture(list: List<CompletableFuture<ViewRenderable>>, escuchadorDelController: ResultListener<List<ViewRenderable>>) {        val listViewRenderable = mutableListOf<ViewRenderable>()        var i = 0        list.forEach {            CompletableFuture.allOf(                    it            ).handle { t, u ->                listViewRenderable.add(it.get())                i +=1                if (i == list.size){                    escuchadorDelController.finish(listViewRenderable)                }            }        }    }}